import React, { useState, useEffect, useRef, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Plus, X, Upload, Download, Trash2, Edit3, Link as LinkIcon, Clipboard as ClipboardIcon, Image as ImageIcon } from "lucide-react";
import { Moon, Sun, Sparkles } from "lucide-react";
import ParticleGallery from "./ParticleGallery";
import { 
  getAllPosters, 
  saveAllPosters, 
  deletePoster as dbDeletePoster, 
  clearAllPosters, 
  migrateFromLocalStorage 
} from "./db";

const LS_KEY = "cyberwall.posters.v1";

function useLocalData() {
  const [items, setItems] = useState([]);

  // === 内置默认海报 ===
  const defaultPosters = [
    {
      "id": "lalaland-2025-11-02-15-45-27-1762069568111",
      "title": "Lalaland｜中文海报（样例）",
      "posterUrl": "data:image/jpeg;base64,",
      "review": "City of stars..."
    },
  ]

  const [isLoading, setIsLoading] = useState(true);

  // === 初始化：从 IndexedDB 加载数据（支持 localStorage 迁移）===
  useEffect(() => {
    const initData = async () => {
      try {
        // 1. 尝试从旧版 localStorage 迁移数据
        await migrateFromLocalStorage(LS_KEY);

        // 2. 从 IndexedDB 加载数据
        const stored = await getAllPosters();

        if (stored && stored.length > 0) {
          setItems(stored);
        } else {
          // 3. 若无数据，加载默认样例并保存到 IndexedDB
          setItems(defaultPosters);
          await saveAllPosters(defaultPosters);
        }
      } catch (err) {
        console.warn("⚠️ Failed to load posters from IndexedDB:", err);
        setItems(defaultPosters);
      } finally {
        setIsLoading(false);
      }
    };

    initData();
  }, []); // <-- ✅ 空依赖数组：仅运行一次

  // === 保存数据到 IndexedDB 的方法 ===
  const saveItems = useCallback(async (newItems) => {
    setItems(newItems);
    try {
      await saveAllPosters(newItems);
    } catch (err) {
      console.error("❌ Failed to save posters to IndexedDB:", err);
    }
  }, []);

  return { items, setItems, saveItems, isLoading };
}

function blobToCompressedDataURL(blob, { maxWidth = 800, quality = 0.85 } = {}) {
  return new Promise((resolve) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      const img = new Image();
      img.onload = () => {
        const scale = Math.min(1, maxWidth / img.width);
        const canvas = document.createElement("canvas");
        canvas.width = img.width * scale;
        canvas.height = img.height * scale;
        const ctx = canvas.getContext("2d");
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
        const dataURL = canvas.toDataURL("image/jpeg", quality);
        resolve(dataURL);
      };
      img.crossOrigin = "anonymous";
      img.src = e.target.result;
    };
    reader.readAsDataURL(blob);
  });
}

async function compressFromFile(file) {
  return await blobToCompressedDataURL(file);
}

async function compressFromUrl(url) {
  const res = await fetch(url, { mode: "cors" });
  if (!res.ok) throw new Error("Failed to fetch image");
  const blob = await res.blob();
  return await blobToCompressedDataURL(blob);
}

function Drawer({ open, onClose, items, setItems, saveItems, onAdd, editingItem }) {
  const [title, setTitle] = useState("");
  const [posterUrl, setPosterUrl] = useState("");
  const [review, setReview] = useState("");
  const [urlInput, setUrlInput] = useState("");
  const imgInputRef = useRef(null);
  const jsonInputRef = useRef(null);

  useEffect(() => {
    if (editingItem) {
      setTitle(editingItem.title);
      setPosterUrl(editingItem.posterUrl);
      setReview(editingItem.review);
    } else if (open) {
      setTitle("");
      setPosterUrl("");
      setReview("");
      setUrlInput("");
    }
  }, [editingItem, open]);

  useEffect(() => {
    const handlePaste = (e) => {
      const file = e.clipboardData?.files?.[0];
      if (file && file.type.startsWith("image/")) {
        compressFromFile(file).then((dataURL) => setPosterUrl(dataURL));
      }
    };
    window.addEventListener("paste", handlePaste);
    return () => window.removeEventListener("paste", handlePaste);
  }, []);

  const handleFileChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) return;
    compressFromFile(file).then((dataURL) => setPosterUrl(dataURL));
  };

  const handleFetchFromUrl = async () => {
    if (!urlInput) return;
    try {
      const dataURL = await compressFromUrl(urlInput);
      setPosterUrl(dataURL);
    } catch (err) {
      alert("❌ Failed to fetch image (CORS or invalid link)");
    }
  };

  const submit = () => {
    if (!title || !posterUrl) return;
    if (editingItem) {
      const updated = items.map((p) =>
        p.id === editingItem.id ? { ...p, title, posterUrl, review } : p
      );
      setItems(updated);
    } else {
      const newPoster = {
        id: title.toLowerCase().replace(/[^a-z0-9]+/g, "-") + "-" + Date.now(),
        title,
        posterUrl,
        review,
      };
      onAdd(newPoster);
    }
    onClose();
  };

  /** 删除逻辑 **/
  const handleDelete = async () => {
    if (!editingItem) return;
    if (confirm(`⚠️ Delete poster "${editingItem.title}"? This cannot be undone.`)) {
      const filtered = items.filter((p) => p.id !== editingItem.id);
      await saveItems(filtered);
      await dbDeletePoster(editingItem.id);
      alert("🗑️ Poster deleted successfully.");
      onClose();
    }
  };

  const importJson = async (e) => {
    const file = e.target.files?.[0];
    if (!file) return;
    try {
      let text = await file.text();
      text = text.trim().replace(/^\uFEFF/, ""); // 去掉 BOM & 空格
      const parsed = JSON.parse(text);
  
      if (Array.isArray(parsed)) {
        // === 获取当前数据 ===
        const current = Array.isArray(items) ? items : [];
  
        // === 合并并去重（以 id 作为唯一标识）===
        const merged = [...current, ...parsed];
        const unique = Array.from(new Map(merged.map(p => [p.id, p])).values());
  
        // === 更新状态与存储到 IndexedDB ===
        await saveItems(unique);
  
        const newCount = unique.length - current.length;
        alert(`✅ Imported ${parsed.length} posters (${newCount} new, ${unique.length} total).`);
      } else {
        alert("⚠️ Invalid JSON structure (must be an array).");
      }
    } catch (err) {
      console.error("Import JSON error:", err);
      alert("❌ Failed to parse JSON file. Please ensure it's valid UTF-8 JSON.");
    } finally {
      // 允许重复导入同一文件（清空 input 值）
      e.target.value = "";
    }
  };
  

  const exportJson = () => {
    const blob = new Blob([JSON.stringify(items, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "posters.json";
    a.click();
    URL.revokeObjectURL(url);
  };

  const clearStorage = async () => {
    if (confirm("⚠️ Clear all local data?")) {
      await clearAllPosters();
      setItems([]);
      alert("🧹 IndexedDB storage cleared.");
    }
  };

  return (
    <AnimatePresence>
      {open && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 z-40 bg-black/50"
          onClick={onClose}
        >
          <motion.div
            initial={{ x: 400 }}
            animate={{ x: 0 }}
            exit={{ x: 400 }}
            transition={{ type: "spring", stiffness: 120, damping: 18 }}
            className="absolute right-0 top-0 h-full w-full max-w-md bg-zinc-900 text-zinc-100 p-6 overflow-y-auto shadow-2xl"
            onClick={(e) => e.stopPropagation()}
          >
            {/* 右上角退出按钮 */}
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-xl font-semibold">
                {editingItem ? "Edit Poster" : "Poster Manager"}
              </h3>
              <button onClick={onClose} className="p-2 rounded hover:bg-zinc-800 text-white">
                <X size={18} />
              </button>
            </div>

            <div className="space-y-3">
              {/* 标题 */}
              <label className="block">
                <span className="text-sm text-zinc-300">Title</span>
                <input
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  className="w-full bg-zinc-800 rounded px-3 py-2 outline-none focus:ring-2 focus:ring-cyan-400"
                />
              </label>

              {/* 图片上传 */}
              <div className="space-y-2">
                <span className="text-sm text-zinc-300">Poster Image (URL/Paste/Upload)</span>
                <div className="flex gap-2">
                  <input
                    value={urlInput}
                    onChange={(e) => setUrlInput(e.target.value)}
                    placeholder="Enter Image URL or Ctrl+V here"
                    className="flex-1 bg-zinc-800 rounded px-3 py-2 outline-none focus:ring-2 focus:ring-cyan-400"
                  />
                  <button
                    onClick={handleFetchFromUrl}
                    className="px-3 py-2 rounded bg-cyan-800 text-white font-semibold hover:bg-cyan-400"
                  >
                    <LinkIcon size={16} />
                  </button>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => imgInputRef.current?.click()}
                    className="flex-1 inline-flex items-center justify-center gap-2 rounded-xl bg-zinc-800 text-white hover:bg-zinc-700 py-2"
                  >
                    <ImageIcon size={16} /> Upload File
                  </button>
                  <input
                    ref={imgInputRef}
                    type="file"
                    accept="image/*"
                    className="hidden"
                    onChange={handleFileChange}
                  />
                  {/* <div className="flex items-center gap-1 text-xs text-zinc-400">
                    <ClipboardIcon size={14} /> Paste image
                  </div> */}
                </div>
              </div>

              {posterUrl && (
                <div className="mt-2">
                  <img
                    src={posterUrl}
                    alt="Preview"
                    className="rounded-xl border border-zinc-700 max-h-64 mx-auto"
                  />
                </div>
              )}

              {/* 简短评论 */}
              <label className="block">
                <span className="text-sm text-zinc-300">Short Review</span>
                <textarea
                  value={review}
                  onChange={(e) => setReview(e.target.value)}
                  rows={3}
                  className="w-full bg-zinc-800 rounded px-3 py-2 outline-none focus:ring-2 focus:ring-cyan-400"
                />
              </label>

              {/* 添加或保存 */}
              <button
                onClick={submit}
                className="mt-2 w-full inline-flex items-center justify-center gap-2 rounded-xl bg-cyan-800 hover:bg-cyan-400 text-white font-semibold py-2"
              >
                <Plus size={16} /> {editingItem ? "Save Changes" : "Add Poster"}
              </button>

              {/* ✅ 新增删除按钮（仅编辑状态显示） */}
              {editingItem && (
                <button
                  onClick={handleDelete}
                  className="w-full flex items-center justify-center gap-2 rounded-xl bg-red-800 hover:bg-red-500 text-white font-semibold py-2"
                >
                  <Trash2 size={16} /> Delete Poster
                </button>
              )}

              <hr className="my-4 border-zinc-700" />

              {/* 导入导出与清除 */}
              <button
                onClick={() => jsonInputRef.current?.click()}
                className="w-full flex items-center justify-center gap-2 rounded-xl bg-zinc-800 hover:bg-zinc-700 py-2 text-white"
              >
                <Upload size={16} /> Import Posters JSON
              </button>
              <input
                ref={jsonInputRef}
                type="file"
                accept="application/json"
                className="hidden"
                onChange={importJson}
              />
              <button
                onClick={exportJson}
                className="w-full flex items-center justify-center gap-2 rounded-xl bg-zinc-800 hover:bg-zinc-700 py-2 text-white"
              >
                <Download size={16} /> Export Posters JSON
              </button>
              <button
                onClick={clearStorage}
                className="w-full flex items-center justify-center gap-2 rounded-xl bg-red-800 hover:bg-red-400 text-white py-2"
              >
                <Trash2 size={16} /> Clear IndexedDB
              </button>
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}


// poster 卡片的翻转效果
function FlipCard({ poster, onEdit }) {
  const [flipped, setFlipped] = useState(false);

  if (!poster || typeof poster !== 'object') return null;
  const { posterUrl = '', title = 'Untitled', review = '' } = poster;

  return (
    <div
      className="relative w-full aspect-[2/3] perspective cursor-pointer"
      onClick={() => setFlipped(!flipped)}
      onMouseEnter={() => setFlipped(true)}
      onMouseLeave={() => setFlipped(false)}
    >
      <motion.div
        className="absolute inset-0 preserve-3d transition-transform duration-700"
        style={{
          transformStyle: 'preserve-3d',
          transform: flipped ? 'rotateY(180deg)' : 'rotateY(0deg)',
        }}
      >
        {/* 正面 */}
        <div className="absolute inset-0 backface-hidden">
          <img
            src={posterUrl}
            alt={title}
            className={`h-full w-full object-cover rounded-2xl border border-zinc-800 transition-all duration-700 ${flipped ? 'blur-sm scale-105' : ''}`}
          />
        </div>

        {/* 反面 */}
        {/* === 卡片背面：电影海报风格 === */}
        <div
          className="absolute inset-0 rounded-2xl border border-zinc-800 p-6 flex flex-col items-center justify-between backface-hidden"
          style={{
            transform: 'rotateY(180deg)',
            background:
              'radial-gradient(circle at 50% 20%, rgba(255,255,220,0.08), rgba(0,0,0,0.95))',
            boxShadow: '0 -5px 40px rgba(255, 255, 180, 0.2)',
            // fontFamily: '"Playfair Display", "Noto Serif SC", serif',
            letterSpacing: '0.3px',
          }}
        >
          {/* 标题 */}
          <h3
            className="text-2xl font-semibold tracking-wide mb-6"
            style={{
              color: '#fff7d1',
              textShadow:
                '0 0 12px rgba(255, 245, 160, 0.45), 0 0 20px rgba(255, 255, 200, 0.25)',
              letterSpacing: '0.05em',
            }}
          >
            {title}
          </h3>

          {/* 短评 */}
          <p
            className="text-center text-base leading-relaxed text-zinc-100 px-2 line-clamp-6"
            style={{
              maxWidth: '90%',
              fontSize: '0.95rem',
              color: '#f8f8f2',
              textShadow:
                '0 0 6px rgba(255, 255, 200, 0.15), 0 0 12px rgba(255, 255, 180, 0.1)',
              fontFamily: '"DM Sans", "Noto Serif SC", serif',
              opacity: 0.92,
            }}
          >
            {review || '“Every story has a star.”'}
          </p>

          {/* 分割线 */}
          <div className="w-12 h-[1px] bg-zinc-600/40 mt-6 mb-4"></div>

          {/* 编辑按钮 */}
          <button
            onClick={(e) => {
              e.stopPropagation();
              onEdit && onEdit(poster);
            }}
            // className="inline-flex items-center gap-2 px-4 py-2 rounded-full text-sm font-medium transition-all duration-300"
            className="mt-4 inline-flex items-center gap-2 px-3 py-2 rounded-xl bg-white-500 text-white font-semibold hover:bg-cyan-800"
            style={{
              background:
                'linear-gradient(90deg, rgba(244,244,244,0.1) 0%, rgba(255,255,255,0.15) 100%)',
              color: '#fffbe9',
              border: '1px solid rgba(255,255,200,0.25)',
              backdropFilter: 'blur(4px)',
            }}
            onMouseEnter={(e) => (e.currentTarget.style.background = 'rgba(255,255,255,0.25)')}
            onMouseLeave={(e) => (e.currentTarget.style.background = 'rgba(255,255,255,0.15)')}
          >
            <Edit3 size={18} /> Edit
          </button>
        </div>


      </motion.div>
    </div>
  );
}


export default function App() {
  const { items, setItems, saveItems, isLoading } = useLocalData();
  const [drawer, setDrawer] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [darkMode, setDarkMode] = useState(true);
  const [particleGalleryOpen, setParticleGalleryOpen] = useState(false);

  // 加载中显示
  if (isLoading) {
    return (
      <div className="min-h-screen w-full flex items-center justify-center bg-zinc-950 text-zinc-100">
        <div className="flex flex-col items-center gap-4">
          <div className="w-12 h-12 border-4 border-cyan-500 border-t-transparent rounded-full animate-spin" />
          <p className="text-zinc-400">Loading posters...</p>
        </div>
      </div>
    );
  }

  return (
    <div
      className={`min-h-screen w-full transition-colors duration-700 ${
        darkMode
          ? "bg-[radial-gradient(1200px_600px_at_50%_-10%,rgba(34,211,238,0.1),transparent),radial-gradient(1200px_600px_at_80%_120%,rgba(244,63,94,0.1),transparent)] text-zinc-100 bg-zinc-950"
          : "bg-[radial-gradient(1200px_600px_at_50%_-10%,rgba(34,211,238,0.05),transparent),radial-gradient(1200px_600px_at_80%_120%,rgba(244,63,94,0.05),transparent)] text-zinc-900 bg-zinc-50"
      }`}
    >
    
    {/* 此处可以通过 ml-15 与 mr-15 调整标题内容间距，左右边距，实现全面屏 */}
    <header className="sticky top-0 z-20 backdrop-blur border-b border-zinc-800/60 bg-zinc-950/60 ">
      <div className="mx-auto max-w-6xl px-4 py-3 flex items-center justify-between">
        <h1 className="text-lg md:text-2xl font-semibold tracking-wide">
          🎞️ Cyber Poster Wall 
        </h1>
        <div className="flex items-center gap-3 ">

          {/* 黑夜模式按钮 */}
          <button
            onClick={() => setDarkMode(!darkMode)}
            className={`inline-flex items-center gap-2 rounded-xl px-3 py-2 transition-colors ${
              darkMode
                ? "border border-zinc-700 hover:border-cyan-400 text-white"
                : "border border-zinc-400 hover:border-cyan-500 text-white"
            }`}
          >
            {darkMode ? <Sun size={16} /> : <Moon size={16} />}
          </button>

          {/* 粒子画廊按钮 */}
          <button
            onClick={() => setParticleGalleryOpen(true)}
            className={`inline-flex items-center gap-2 rounded-xl px-3 py-2 transition-colors ${
              darkMode
                ? "border border-zinc-700 hover:border-purple-400 text-white hover:text-purple-300"
                : "border border-zinc-400 hover:border-purple-500 text-zinc-700 hover:text-purple-500"
            }`}
            title="Particle Gallery"
          >
            <Sparkles size={16} />
          </button>
          
          {/* poster manage 按钮 */}
          <button
            onClick={() => {
              setEditingItem(null);
              setDrawer(true);
            }}
            className="inline-flex items-center gap-2 rounded-xl bg-cyan-800 text-white px-3 py-2 hover:bg-cyan-400"
          >
            <Plus size={16} /> Add / Manage
          </button>
        </div>
      </div>
    </header>

      {/*  poster 展板部分主体部分几行几列 */}
      <main className="mx-auto max-w-6xl px-4 py-6 space-y-6">
        <div className="grid gap-4 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
          {items.map((p) => (
            <FlipCard
              key={p.id}
              poster={p}
              onEdit={(poster) => {
                setEditingItem(poster);
                setDrawer(true);
              }}
            />
          ))}
        </div>
      </main>

      {/* 末尾的提示语 */}
      {/* 此处可以通过通过一个很长的提示语拉开左右边距，实现全面屏 */}
      <footer className="mx-auto max-w-6xl px-4 py-10 text-m text-zinc-500 text-center space-y-3 border-t border-zinc-800/50">
        <p>
          📸 <strong>Pipeline:</strong> Click <b>Add / Manage</b> posters → Import your <code>posters.json</code> → Add / Edit → Export JSON → Clear storage.  
          Fully offline, local-first, and portable.
        </p>

        <p className="italic text-zinc-400 mt-4">
          “Less is more.” — Ludwig Mies van der Rohe  
        </p>

        <div className="text-xs text-zinc-500 mt-4 space-y-1">
          <p>
            <strong></strong> Built with <b>React + Vite + TailwindCSS + Framer Motion</b>.  
            Icons by <b>Lucide</b>. Offline persistence via <code>IndexedDB</code>. Designed for <b>GitHub Pages</b> / <b>Vercel</b> static hosting.
          </p>
          <p>
            ⚖️ <strong>License:</strong> MIT License — free for personal and educational use.  
            © {new Date().getFullYear()} Henry Zhang. All rights reserved.
          </p>
        </div>
      </footer>


      
      <Drawer
        open={drawer}
        onClose={() => setDrawer(false)}
        items={items}
        setItems={setItems}
        saveItems={saveItems}
        onAdd={(p) => saveItems([p, ...items])}
        editingItem={editingItem}
      />

      {/* 粒子画廊 */}
      <ParticleGallery
        images={items}
        isOpen={particleGalleryOpen}
        onClose={() => setParticleGalleryOpen(false)}
      />
    </div>
  );
}
